import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';
import SentimentScore from './Sentimentscore';
import { Link } from 'react-router-dom';
import newsData from './News'; // Import the news data

const entity = 'TSMC';

const EntityNews = ({ entity }) => {
  const styles = {
    newsBox: {
      position: 'relative',
      height: '300px',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    newsHeader: {
      fontSize: 'calc(10px + 1vw)', // Dynamic font size
      fontWeight: 'bold',
      color: '#555555',
    },
    newsSummary: {
      fontSize: 'calc(12px + 0.5vw)', // Dynamic font size
      color: '#555555',
    },
    newsDate: {
      fontSize: 'calc(12px + 0.5vw)', // Dynamic font size
      color: '#555555',
    },
    sentimentScore: {
      fontSize: '14px',
      color: '#4CAF50',
      fontWeight: 'bold',
      position: 'absolute',
      top: '20px',
      right: '10px',
    },
  };

  // Filter news items based on the entity
  const filteredNews = newsData.filter((newsItem) => {
    try {
      // Parse the stringified JSON and access the nested entities array
      const parsedEntities = JSON.parse(newsItem.entities)?.entities || [];
      return parsedEntities.includes(entity);
    } catch (error) {
      console.error("Error parsing entities:", error);
      return false; // If parsing fails, exclude the item
    }
  });
  

  return (
    <Container fluid className="entity-news-container">
      <Row>
        {filteredNews.map((newsItem) => (
          <Col key={newsItem.id} md={5} className="mb-4 ml-4">
            <Link
              to="/Individualnewspage"
              state={{ id: newsItem.id }} // Passing the id here
              key={newsItem.id}
              style={{ textDecoration: 'none' }}
            >
              <div style={styles.newsBox} className="news-box">
                <h4 style={styles.newsHeader}>{newsItem.title}</h4>
                <div style={styles.sentiment}>
                  <SentimentScore newsId={newsItem.id} />
                </div>
                <h4 style={styles.newsDate}>{new Date(newsItem.published_date).toLocaleDateString()}</h4>
                <p style={styles.newsSummary}>{newsItem.description}</p>
              </div>
            </Link>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default EntityNews;
