import React from 'react';
import NewsHeader from './newsheader';
import NewsSummary from './newssummary';
import PropTypes from 'prop-types'; // For type-checking props

// const NewsBox = ({ newsItem }) => {
//   return (
//     <div className="news-box">
//       <NewsHeader title={newsItem.title } /> 
//       <NewsSummary summary={newsItem.summary } /> 
//     </div>
//   );
// };



// export default NewsBox;

const NewsBox = ({ sentimentScore, newsHeader, newsContent }) => {
  // Add logic to change box color based on sentiment score
  let sentimentColor = 'gray'; // Default color

  if (sentimentScore > 0) {
    sentimentColor = 'green'; // Positive sentiment
  } else if (sentimentScore < 0) {
    sentimentColor = 'red'; // Negative sentiment
  }

  return (
    <div style={{ border: '1px solid #ccc', padding: '16px', margin: '16px', borderRadius: '8px', backgroundColor: '#f9f9f9' }}>
      <h3 style={{ color: sentimentColor }}>{newsHeader}</h3>
      <p>{newsContent}</p>
      <p><strong>Sentiment Score: </strong><span style={{ color: sentimentColor }}>{sentimentScore}</span></p>
    </div>
  );
};

// Type-checking the props
NewsBox.propTypes = {
  sentimentScore: PropTypes.number.isRequired,
  newsHeader: PropTypes.string.isRequired,
  newsContent: PropTypes.string.isRequired
};

export default NewsBox;
