import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col } from 'react-bootstrap';
import SentimentScore from './Sentimentscore';
import { Link } from 'react-router-dom';

// Dummy news data
const newsData = [
  {
    "id": 136,
    "publisher": "Yahoo Finance",
    "description": "Taiwan Semiconductor Manufacturing Company Limited TSM, better known as TSMC, saw its shares scale northward...",
    "published_date": "2025-01-23T12:05:00.000Z",
    "title": "3 Reasons to Buy TSMC Stock Beyond Record Q4 Net Profit - Yahoo Finance",
    "url": "https://finance.yahoo.com/news/3-reasons-buy-tsmc-stock-200500471.html",
    "entities": "{\"entities\": [\"TSMC\"]}",
    "sentiment": null
  },
  {
    "id": 137,
    "publisher": "Arizona Big Media",
    "description": "Reinforcing a commitment to invest locally in new pathways into the semiconductor industry, today TSMC Arizona was joined by Governor...",
    "published_date": "2025-01-23T04:10:30.000Z",
    "title": "TSMC Arizona offers new jobs via Technician Apprentice Program - Arizona Big Media",
    "url": "https://azbigmedia.com/business/tsmc-arizona-offers-new-jobs-via-technician-apprentice-program/",
    "entities": "{\"entities\": [\"TSMC\"]}",
    "sentiment": null
  },
  {
    "id": 138,
    "publisher": "AOL",
    "description": "TSMC said on Tuesday that all its sites were operating following an overnight 6.4 magnitude earthquake in southern Taiwan...",
    "published_date": "2025-01-23T18:19:20.000Z",
    "title": "TSMC says all its sites operating following Taiwan quake - AOL",
    "url": "https://www.aol.com/news/tsmc-says-sites-operating-following-040739515.html",
    "entities": "{\"entities\": [\"TSMC\"]}",
    "sentiment": null
  },
  {
    "id": 139,
    "publisher": "TrendForce",
    "description": "After the 6.4 earthquake on January 21, Taiwanese foundry giant TSMC reportedly managed to restore operations...",
    "published_date": "2025-01-23T18:36:00.000Z",
    "title": "[News] TSMC Reportedly Expects Fab 18 Recovery by Jan. 23, Fab 14 Timeline Uncertain After 6.4 Earthquake - TrendForce",
    "url": "https://www.trendforce.com/news/2025/01/24/news-tsmc-reportedly-expects-fab-18-recovery-by-jan-23-fab-14-timeline-uncertain-after-6-4-magnitude-earthquake/",
    "entities": "{\"entities\": [\"TSMC\"]}",
    "sentiment": null
  },
  {
    "id": 140,
    "publisher": "DIGITIMES",
    "description": "Save my User ID and Password\n\nSome subscribers prefer to save their log-in information...",
    "published_date": "2025-01-23T21:48:24.000Z",
    "title": "Trump policies drive supply chain shift to Arizona - DIGITIMES",
    "url": "https://www.digitimes.com/news/a20250122PD220/arizona-production-tsmc-supply-chain-market.html",
    "entities": "{\"entities\": [\"TSMC\"]}",
    "sentiment": null
  }
];

const entity = 'TSMC';

const EntityNews = ({ entity }) => {
  const styles = {
    newsBox: {
      position: 'relative',
      height: '300px',
      borderRadius: '8px',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    },
    newsHeader: {
      fontSize: 'calc(10px + 1vw)', // Dynamic font size
      fontWeight: 'bold',
      color: '#555555',
    },
    newsSummary: {
      fontSize: 'calc(12px + 0.5vw)', // Dynamic font size
      color: '#555555',
    },
    newsDate: {
      fontSize: 'calc(12px + 0.5vw)', // Dynamic font size
      color: '#555555',
    },
    sentimentScore: {
      fontSize: '14px',
      color: '#4CAF50',
      fontWeight: 'bold',
      position: 'absolute',
      top: '20px',
      right: '10px',
    },
  };

  // Filter news items based on the entity
  const filteredNews = newsData.filter(newsItem => {
    try {
      // Parse the entities and check if the entity is included in the list
      const parsedEntities = JSON.parse(newsItem.entities).entities || [];
      return parsedEntities.includes(entity);
    } catch (e) {
      return false; // If there's an error parsing, exclude this item
    }
  });

  return (
    <Container fluid className="entity-news-container">
      <Row>
        {filteredNews.map((newsItem) => (
          <Col key={newsItem.id} md={5} className="mb-4 ml-4">
            <Link
              to="/Individualnewspage"
              state={{ id: newsItem.id }} // Passing the id here
              key={newsItem.id}
              style={{ textDecoration: 'none' }}
            >
              <div style={styles.newsBox} className="news-box">
                <h4 style={styles.newsHeader}>{newsItem.title}</h4>
                <div style={styles.sentiment}>
                  <SentimentScore newsId={newsItem.id} />
                </div>
                <h4 style={styles.newsDate}>{new Date(newsItem.published_date).toLocaleDateString()}</h4>
                <p style={styles.newsSummary}>{newsItem.description}</p>
              </div>
            </Link>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default EntityNews;
