import React from 'react';
import { useLocation } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import Entity from './components/Entity';
import Sentimentscore from './components/Sentimentscore';
import Entitynews from './components/Entitynews';
import Newspage from './pages/Newspage';
import Individualnewspage from './pages/Individualnewspage';
import Entitiespage from './pages/Entitiespage'; 
import Price from './components/Price';
import './App.css';

function App() {
  const location = useLocation();

  const containerStyle = {
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    position: 'fixed',
    top: '100px',
    left: '669px',
  };

  return (
    <div className="App">
      <header className="App-header">
        <Header />
        <Sidebar />

        {/* Conditionally render components if on the root path ("/") */}
        {location.pathname === '/' && (
          <>
            <div style={containerStyle}>
              <Entity />
              <Price />
              <Sentimentscore />
            </div>
            <Entitynews />
          </>
        )}

        {/* Define the Routes for other pages */}
        <Routes>
          <Route path="/" element={<div>Home Page Content</div>} /> {/* Optional home page route */}
          <Route path="/Entitiespage" element={<Entitiespage />} />
          <Route path="/Newspage" element={<Newspage />} />
          <Route path="/Individualnewspage" element={<Individualnewspage />} />
        </Routes>
      </header>
    </div>
  );
}

export default App;
